import { useState } from "react";
import janePhoto from "../images/jane.jpg"
import johnPhoto from "../images/john.jpg"


const ContactList = () => {
const [contacts,setContacts] = useState([{id: 1,img: janePhoto,name: "Jane",phoneNum: "374-2312-245"},{id: 2,img: johnPhoto,name: "John",phoneNum: "234-0347-284"},{id: 3,img: janePhoto,name: "Jane",phoneNum: "374-2312-245"},{id: 4,img: johnPhoto,name: "John",phoneNum: "234-0347-284"},{id: 5,img: janePhoto,name: "Jane",phoneNum: "374-2312-245"},{id: 6,img: johnPhoto,name: "John",phoneNum: "234-0347-284"},{id: 7,img: janePhoto,name: "Jane",phoneNum: "374-2312-245"},{id: 2,img: johnPhoto,name: "John",phoneNum: "234-0347-284"}])
// const [photo,setPhoto] = useState([])
const contactsDelete = (id) => {
    const newContactList = contacts.filter(contact => contact.id != id)
    setContacts(newContactList)
}

    return(
        <div className="contactList">
                { contacts.map((contact) => (
                <div className="contactPreview" key={contact.id}>
                    <img src={contact.img}/>
                    <h2>{ contact.name }</h2>
                    <p>{ contact.phoneNum }</p>
                    <button>Изменить</button>
                    <button onClick={() => contactsDelete(contact.id)}>Удалить</button>
                </div>
            ))}
        </div> 
    )
}
export default ContactList
