import { useState } from "react"
import defaultPicture from "../images/defaultImg.jpeg"
// creating contacts
const CreateContacts = () => {
    const [photo,setPhoto] = useState(defaultPicture)

    // changing image preview
    const handleChangePhoto = (e) => {
        setPhoto(URL.createObjectURL(e.target.files[0]))
    } 

    return(
        <div className="createContacts">
            <h1>Добавить Контакт</h1>
            <form className="contactsForm" action="">
            {/* image */}
            <div className="left">
                <img src={photo} className="imgExamp"/>
                <label id="photoInputLabel" htmlFor="photoInput">Добавить Фотографию</label>
                <input id="photoInput" type="file" accept="image/*" onChange={handleChangePhoto}/>
            </div>

            {/* Name & number */}
            <div className="right">
                <input type="text" id="name" placeholder="Имя"/>
                <input type="number" id="phoneNumber" placeholder="Номер телефона"/>
            </div>
            <button е>Сохранить</button>
            </form>
        </div>
    )
}
export default CreateContacts
